                //FLavor Text
                Console.Clear();
                Console.WriteLine();
                FlavorText.FlavorPicker(ref currentPlanet);
                Console.WriteLine();
                Console.Clear();
                PlanetMenus.Menus(StarChart, ref currentPlanet, Titanium, Gold, Platinum, Vibranium, inventory, Food, Water, Textiles, Electronics, SpareParts, CapturedHydrogen, PoloniumFuel, Spice, Weapons, ref Money);
                
            }
        }

        public double TravelDistance(List<Planet> StarChart, ref int currentPlanet, int nextPlanet)
        {
            var distance = 0.0;
            var x = StarChart[currentPlanet].Coord.x;
            var y = StarChart[currentPlanet].Coord.y;

            var x2 = StarChart[nextPlanet].Coord.x;
            var y2 = StarChart[nextPlanet].Coord.y;

            distance = Math.Sqrt(((x2 - x) * (x2 - x)) + ((y2 - y) * (y2 - y)));



            return distance;
        }

    }
